<?php

namespace Witzke\FacturaBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * LocalidadRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class LocalidadRepository extends EntityRepository {

    public function findByTerm($term) {
        $query = $this->getEntityManager()->createQuery("
SELECT localidad.id as id, localidad.descripcion as descripcion
FROM FacturaBundle:Localidad localidad
WHERE localidad.descripcion LIKE :term
")->setParameter('term', '%' . $term . '%');
        return $query->getArrayResult();
    }

    public function findByProvinciaId($provincia_id) {
        $query = $this->getEntityManager()->createQuery("
SELECT localidad
FROM FacturaBundle:Localidad localidad
LEFT JOIN localidad.provincia provincia
WHERE provincia.id = :provincia_id
")->setParameter('provincia_id', $provincia_id);
        return $query->getArrayResult();
    }

}

